function transform_probs_to_coords(motif, charnames; alphabet_coords=ALPHABET_GLYPHS)
    all_coords = []
    # for each character (row) collect all positions and heights of that character's polygon
    for (j, c) in enumerate(charnames)
        xs, ys = Float64[], Float64[]
        # get character glyph coords else fall back to a simple rectangle
        charglyph = get(alphabet_coords, c, BASIC_RECT)
        # for each postion in the sequence push in the coords for the character's polygon and adjust y_height based on the motif's weight
        for  (xoffset,col) in enumerate(eachcol(motif))
            yoffset = sum(col[col .< col[j]])
            push!(xs, (1.2 .* charglyph.x .+ xoffset .- .5)...)
            push!(xs, NaN)
            push!(ys, (col[j] .* charglyph.y .+ yoffset)...)
            push!(ys, NaN)
        end
        push!(all_coords, (c, (;c=[c], xs, ys)))
    end
    all_coords
end

@userplot LogoPlot

"""
    logoplot(motif::AbstractMatrix, charnames::AbstractVector{AbstractString}; alphabet_coords=ALPHABET_GLYPHS, do_norm=false, ignore_case=false)
"""
@recipe function f(data::LogoPlot; alphabet_coords=ALPHABET_GLYPHS, do_norm=false, ignore_case=false)
    motif = data.args[1]
    charnames = data.args[2]

    length(charnames) != size(motif, 1) && throw(ArgumentError("number of rows in `motif` matrix does not match length of `charnames`, motif has $(size(motif,1)) rows and charnames has length $(length(charnames))"))
    
    if do_norm
        motif = motif ./ sum(motif, dims=1)
    end

    if ignore_case
        charnames = uppercase.(charnames)
    end

    all_coords = transform_probs_to_coords(motif, charnames; alphabet_coords)

    xticks_pos = size(motif,2) < 20 ? (1:size(motif,2)) : [1, (0:5:size(motif,2))[2:end]...]
    
    grid --> false 
    tickdir --> :out
    framestyle --> :box
    xticks --> xticks_pos
    # legend --> :outerright
    # margin --> 5Plots.mm
    for (k, v) in all_coords
        @series begin
            fill := 0
            lw --> 0
            label --> k
            color --> get(AA_PALETTE, k, :grey)
            v.xs, v.ys
        end
    end
end

end
